PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE locations (id INTEGER PRIMARY KEY AUTOINCREMENT, name TEXT, room TEXT);
CREATE TABLE items (id INTEGER PRIMARY KEY AUTOINCREMENT, location_id INTEGER, name TEXT, category TEXT, quantity FLOAT, notes TEXT);
CREATE TABLE recipes (
    recipe_id INTEGER PRIMARY KEY AUTOINCREMENT,
    recipe_name TEXT,
    cuisine TEXT,
    serving_size INTEGER,
    prep_time INTEGER,
    cook_time INTEGER,
    total_time INTEGER,
    instructions TEXT,
    additional_notes TEXT,
    img_str_file TEXT
, deleted boolean default false, recipe_search_id INTEGER, favorite BOOLEAN DEFAULT FALSE, url TEXT, video_tips TEXT);
CREATE TABLE recipe_to_ingredients (
    recipe_id INTEGER,
    ingredient_id INTEGER,
    quantity FLOAT,
    unit TEXT
);
CREATE TABLE migration_log (id INTEGER PRIMARY KEY, version INTEGER, created_at TEXT );
PRAGMA writable_schema=ON;
CREATE TABLE IF NOT EXISTS 'recipe_search_data'(id INTEGER PRIMARY KEY, block BLOB);
CREATE TABLE IF NOT EXISTS 'recipe_search_idx'(segid, term, pgno, PRIMARY KEY(segid, term)) WITHOUT ROWID;
CREATE TABLE IF NOT EXISTS 'recipe_search_content'(id INTEGER PRIMARY KEY, c0, c1, c2, c3);
CREATE TABLE IF NOT EXISTS 'recipe_search_docsize'(id INTEGER PRIMARY KEY, sz BLOB);
CREATE TABLE IF NOT EXISTS 'recipe_search_config'(k PRIMARY KEY, v) WITHOUT ROWID;
CREATE TABLE ingredients (id INTEGER PRIMARY KEY AUTOINCREMENT,name TEXT,category TEXT,quantity FLOAT,unit TEXT,perishable TEXT,last_updated TEXT);
DELETE FROM sqlite_sequence;
PRAGMA writable_schema=OFF;
COMMIT;
